import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import Dashboard from './components/Dashboard';
import SuperAdmin from './components/SuperAdmin';
import ProtectedRoute from './components/ProtectedRoute';
import SearchComponent from './components/SearchComponent';
import StoryModal from './components/StoryModal';
import { AuthProvider } from './context/AuthContext';
import { User, Home, BarChart3, ArrowLeft, BookOpen, Brain } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './App.css';

// API Base URL
const API_BASE_URL = 'http://127.0.0.1:8000/api';

// API Functions
const apiCall = async (endpoint, options = {}) => {
  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers
      },
      ...options
    });
    return await response.json();
  } catch (error) {
    console.error('API Error:', error);
    return null;
  }
};

// Component for the main homepage
const Homepage = ({ stories, onStoryClick, onStorySelect, isModalOpen, selectedStory, onCloseModal }) => {
  const categorizeStories = () => {
    if (!stories || stories.length === 0) {
      return {};
    }
    
    const categories = {
      'Popular Stories': stories.filter(story => story.id <= 4),
      'Recommended For You': stories.filter(story => story.id > 4),
      'AI Generated': stories.filter(story => story.source === 'AI'),
      'Human Written': stories.filter(story => story.source === 'Human')
    };
    return categories;
  };

  const categories = categorizeStories();

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <header className="bg-gray-800 p-5 flex justify-between items-center sticky top-0 z-50">
        <h1 className="text-red-500 text-3xl font-bold">Beyond the Words</h1>
        
        {/* Search Component */}
        <div className="flex-1 flex justify-center mx-8">
          <div className="w-80">
            <SearchComponent onStorySelect={onStorySelect} />
          </div>
        </div>

        <nav className="flex space-x-6">
          <button className="flex items-center space-x-1 hover:text-red-500 transition-colors bg-transparent border-none cursor-pointer text-white">
            <Home className="h-5 w-5" />
            <span>Home</span>
          </button>
          <button className="flex items-center space-x-1 hover:text-red-500 transition-colors bg-transparent border-none cursor-pointer text-white">
            <User className="h-5 w-5" />
            <span>Profile</span>
          </button>
        </nav>
      </header>

      {/* Hero Banner */}
      <div className="relative h-96 overflow-hidden border-b-4 border-red-500 flex items-end p-16">
        {/* Fallback background */}
        <div className="absolute inset-0 bg-gradient-to-r from-purple-900 to-blue-900"></div>
        
        {/* Dark overlay for text readability */}
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>
        
        {/* Text content */}
        <h2 className="relative z-10 text-4xl font-bold bg-black bg-opacity-60 px-6 py-3 rounded-lg">
          Discover Stories: Beyond The Words
        </h2>
      </div>

      {/* Story Categories */}
      <div className="p-8 space-y-8">
        {stories.length === 0 ? (
          <div className="text-center text-gray-400">
            <p>Loading stories...</p>
          </div>
        ) : (
          Object.entries(categories).map(([categoryName, categoryStories]) => (
            categoryStories.length > 0 && (
              <div key={categoryName}>
                <h3 className="text-2xl font-semibold text-red-500 mb-5">{categoryName}</h3>
                <div className="flex overflow-x-auto gap-4 pb-4" style={{scrollBehavior: 'smooth'}}>
                  {categoryStories.map((story) => (
                    <div
                      key={story.id}
                      onClick={() => onStoryClick(story)}
                      className="min-w-48 bg-gray-800 rounded-lg overflow-hidden cursor-pointer transform transition-all duration-300 hover:scale-105 hover:shadow-lg hover:shadow-red-500/20"
                    >
                      <div className="h-60 bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center">
                        <BookOpen className="h-16 w-16 text-white opacity-80" />
                      </div>
                      <div className="p-3">
                        <p className="text-center font-medium">{story.title}</p>
                        <p className="text-center text-sm text-gray-400 mt-1">
                          Age {story.age_group}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )
          ))
        )}
      </div>

      {/* Story Modal */}
      {isModalOpen && selectedStory && (
        <StoryModal 
          story={selectedStory} 
          isOpen={isModalOpen} 
          onClose={onCloseModal} 
        />
      )}

      {/* Footer */}
      <footer className="bg-gray-800 border-t border-gray-700 p-8 text-center text-gray-400">
        © 2025 Beyond the Words. All rights reserved.
      </footer>
    </div>
  );
};

// Component for displaying individual story
