// --- Main Application Component to handle all views and state ---
const CombinedApp = () => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAgeGroup, setSelectedAgeGroup] = useState(null);
  const [currentView, setCurrentView] = useState('landingPage'); // Initial view is now 'landingPage'
  const [selectedStory, setSelectedStory] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [authorshipResult, setAuthorshipResult] = useState(null);
  const [similarStories, setSimilarStories] = useState([]);
  const [loading, setLoading] = useState(false);

  // State for authentication
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  // Load stories and apply initial filtering
  useEffect(() => {
    const fetchStories = async () => {
      setLoading(true);
      try {
        const data = await apiCall('/stories/');
        if (data && Array.isArray(data)) {
          setStories(data);
        } else {
          // Fallback to mock data
          setStories(mockStories);
        }
      } catch (error) {
        console.error('Error fetching stories:', error);
        setStories(mockStories);
      } finally {
        setLoading(false);
      }
    };

    fetchStories();
  }, []);

  // Effect to handle filtering logic when stories, search term, or age group changes
  useEffect(() => {
    let newFilteredStories = [...stories];

    // Apply search filter first
    if (searchTerm) {
      newFilteredStories = newFilteredStories.filter(story =>
        story.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        story.story.toLowerCase().includes(searchTerm.toLowerCase()) ||
        story.age_group.toLowerCase().includes(searchTerm.toLowerCase()) ||
        story.source.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply age group filter second
    if (selectedAgeGroup) {
      newFilteredStories = newFilteredStories.filter(story => story.age_group === selectedAgeGroup);
    }

    setFilteredStories(newFilteredStories);

  }, [stories, searchTerm, selectedAgeGroup]); // Dependencies for this effect

  // Functions for navigation and authentication
  const navigateTo = (page) => {
    setCurrentView(page);
  };

  const handleSignIn = (username, password) => {
    // In a real application, you would send a request to a backend API here
    console.log('Attempting to sign in with:', { username, password });
    setIsLoggedIn(true);
    setCurrentUser(username);
    navigateTo('homepage');
  };

  const handleSignUp = (username, password) => {
    // In a real application, this would register a new user in a database.
    console.log('Attempting to sign up with:', { username, password });
    setIsLoggedIn(true);
    setCurrentUser(username);
    navigateTo('homepage');
  };

  const handleSignOut = () => {
    setIsLoggedIn(false);
    setCurrentUser(null);
    navigateTo('landingPage');
  };

  // Story-related handlers
  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const handleFilterChange = (ageGroup) => {
    setSelectedAgeGroup(ageGroup);
  };

  const handleStoryClick = async (story) => {
    setSelectedStory(story);
    setCurrentView('story');

    try {
      const similar = await apiCall(`/stories/${story.id}/similar/`);
      if (similar && Array.isArray(similar)) {
        setSimilarStories(similar);
      } else {
        const mockSimilar = stories.filter(s => 
          s.id !== story.id && s.age_group === story.age_group
        ).slice(0, 3);
        setSimilarStories(mockSimilar);
      }
    } catch (error) {
      console.error('Error fetching similar stories:', error);
      setSimilarStories([]);
    }
  };

  const handleHomeClick = () => {
    setCurrentView('homepage');
    setSelectedStory(null);
    setSearchTerm('');
    setSelectedAgeGroup(null);
    setFilteredStories(stories);
  };

  const handleDetailedAnalysis = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/analyze/`, { method: 'POST' });

      if (data) {
        setAnalysisData(data);
        setCurrentView('analysis');
      } else {
        const mockAnalysis = {
          word_count: selectedStory.story.split(' ').length,
          sentence_count: selectedStory.story.split('.').length - 1,
          ttr: 0.75,
          flesch_kincaid_grade: 6.2,
          ari_score: 5.8,
          sentiment_label: 'positive',
          sentiment_score: 0.85,
          pos_distribution: [
            { name: 'Nouns', value: 45 },
            { name: 'Verbs', value: 25 },
            { name: 'Adjectives', value: 15 },
            { name: 'Adverbs', value: 10 },
            { name: 'Others', value: 5 }
          ]
        };
        setAnalysisData(mockAnalysis);
        setCurrentView('analysis');
      }
    } catch (error) {
      console.error('Error performing analysis:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAuthorshipCheck = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/detect_authorship/`, { method: 'POST' });

      if (data) {
        setAuthorshipResult(data);
        setCurrentView('authorship');
      } else {
        const mockAuthorship = {
          predicted_source: selectedStory.source === 'AI' ? 'AI' : 'Human',
          confidence_score: 0.92,
          features: {
            repetition_score: 0.15,
            avg_sentence_length: 18.5,
            complexity_score: 0.68,
            punctuation_ratio: 0.08
          }
        };
        setAuthorshipResult(mockAuthorship);
        setCurrentView('authorship');
      }
    } catch (error) {
      console.error('Error checking authorship:', error);
    } finally {
      setLoading(false);
    }
  };

  // Navigate back functions
  const handleBack = () => {
    if (currentView === 'story') {
      setCurrentView('homepage');
      setSelectedStory(null);
    } else {
      setCurrentView('story');
    }
  };

  const handleSimilarStoryClick = (story) => {
    handleStoryClick(story);
  };

  // Render current view based on state
  const renderCurrentView = () => {
    // Show a loading screen if we're fetching data and are not logged in yet.
    if (loading && currentView === 'homepage') {
      return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
          <div className="text-white text-xl">Loading stories...</div>
        </div>
      );
    }

    if (isLoggedIn) {
      // If logged in, show the main app views
      switch (currentView) {
        case 'homepage':
        case 'signIn': // Fallback to homepage if user somehow lands on signIn while logged in
        case 'signUp': // Fallback to homepage if user somehow lands on signUp while logged in
          return (
            <Homepage 
              stories={filteredStories}
              onStoryClick={handleStoryClick}
              onSearch={handleSearch}
              searchTerm={searchTerm}
              onHomeClick={handleHomeClick}
              onFilterChange={handleFilterChange}
              selectedAgeGroup={selectedAgeGroup}
              onSignOut={handleSignOut}
            />
          );
        case 'story':
          return (
            <StoryView
              story={selectedStory}
              onBack={handleBack}
              onDetailedAnalysis={handleDetailedAnalysis}
              onAuthorshipCheck={handleAuthorshipCheck}
              similarStories={similarStories}
              onSimilarStoryClick={handleSimilarStoryClick}
              onHomeClick={handleHomeClick}
            />
          );
        case 'analysis':
          return (
            <div className="min-h-screen bg-gray-900 text-white">
              <header className="bg-gray-800 p-5 flex items-center sticky top-0 z-50">
                <button onClick={handleBack} className="mr-4 hover:text-red-500 transition-colors">
                  <ArrowLeft className="h-6 w-6" />
                </button>
                <h1 className="text-red-500 text-3xl font-bold">Analysis: {selectedStory?.title}</h1>
              </header>
              <div className="max-w-6xl mx-auto p-8">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
                  <div className="bg-gray-800 rounded-lg p-6">
                    <h3 className="text-blue-400 font-semibold mb-2">Word Count</h3>
                    <p className="text-3xl font-bold">{analysisData?.word_count}</p>
                  </div>
                  <div className="bg-gray-800 rounded-lg p-6">
                    <h3 className="text-green-400 font-semibold mb-2">Sentence Count</h3>
                    <p className="text-3xl font-bold">{analysisData?.sentence_count}</p>
                  </div>
                  <div className="bg-gray-800 rounded-lg p-6">
                    <h3 className="text-purple-400 font-semibold mb-2">Lexical Diversity</h3>
                    <p className="text-3xl font-bold">{analysisData?.ttr?.toFixed(3)}</p>
                  </div>
                </div>

                {analysisData?.pos_distribution && (
                  <div className="bg-gray-800 rounded-lg p-6">
                    <h3 className="text-xl font-semibold mb-4 text-left">Part-of-Speech Distribution</h3>
                    <ResponsiveContainer width="100%" height={400}>
                      <BarChart data={analysisData.pos_distribution}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                        <XAxis dataKey="name" stroke="#9CA3AF" />
                        <YAxis stroke="#9CA3AF" />
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: '#1F2937', 
                            border: '1px solid #374151',
                            borderRadius: '8px',
                            color: '#F9FAFB'
                          }} 
                        />
                        <Bar dataKey="value" fill="#EF4444" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                )}
              </div>
            </div>
          );
        case 'authorship':
          return (
            <div className="min-h-screen bg-gray-900 text-white">
              <header className="bg-gray-800 p-5 flex items-center sticky top-0 z-50">
                <button onClick={handleBack} className="mr-4 hover:text-red-500 transition-colors">
                  <ArrowLeft className="h-6 w-6" />
                </button>
                <h1 className="text-red-500 text-3xl font-bold">Authorship: {selectedStory?.title}</h1>
              </header>
              <div className="max-w-4xl mx-auto p-8">
                <div className="bg-gray-800 rounded-lg p-8 text-center">
                  <div className="mb-6">
                    <Brain className="h-16 w-16 mx-auto mb-4 text-purple-400" />
                    <h2 className="text-3xl font-bold mb-4">Authorship Analysis Result</h2>
                  </div>

                  <div className="mb-6">
                    <div className={`inline-flex items-center px-8 py-4 rounded-full text-2xl font-bold ${
                      authorshipResult?.predicted_source === 'AI' 
                          ? 'bg-purple-600 text-white' 
                          : 'bg-green-600 text-white'
                    }`}>
                      {authorshipResult?.predicted_source === 'AI' ? '🤖' : '👤'} {authorshipResult?.predicted_source} Generated
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
                    <div className="bg-gray-700 rounded-lg p-6">
                      <h3 className="text-lg font-semibold mb-2">Confidence Score</h3>
                      <div className="text-3xl font-bold text-blue-400">
                        {((authorshipResult?.confidence_score || 0) * 100).toFixed(1)}%
                      </div>
                    </div>
                    <div className="bg-gray-700 rounded-lg p-6">
                      <h3 className="text-lg font-semibold mb-2">Actual Source</h3>
                      <div className="text-3xl font-bold text-yellow-400">
                        {selectedStory?.source}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        default:
          return <div>Page not found</div>;
      }
    } else {
      // If not logged in, show the authentication views
      switch (currentView) {
        case 'landingPage':
          return <LandingPage navigateTo={navigateTo} />;
        case 'signIn':
          return <SignInPage onSignIn={handleSignIn} navigateTo={navigateTo} />;
        case 'signUp':
          return <SignUpPage onSignUp={handleSignUp} navigateTo={navigateTo} />;
        case 'homepage': // Prevent direct access to homepage without login
        default:
          return <LandingPage navigateTo={navigateTo} />;
      }
    }
  };

  return <div className="CombinedApp">{renderCurrentView()}</div>;
};

// Render the application
