import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import { Search, User, Home, BarChart3, Users, ArrowLeft, BookOpen, Brain, SlidersHorizontal } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

// --- Placeholder Components for Authentication and Admin (not modified) ---
const SignIn = () => <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
  <div className="text-xl">Sign In Page</div>
</div>;
const SignUp = () => <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
  <div className="text-xl">Sign Up Page</div>
</div>;
const Dashboard = () => <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
  <div className="text-xl">Dashboard Page</div>
</div>;
const SuperAdmin = () => <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
  <div className="text-xl">Super Admin Page</div>
</div>;
const ProtectedRoute = ({ children, adminOnly = false }) => children;
const AuthProvider = ({ children }) => children;
// --- End Placeholder Components ---

// API Base URL
const API_BASE_URL = 'http://127.0.0.1:8000/api';

// API Functions
const apiCall = async (endpoint, options = {}) => {
  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers
      },
      ...options
    });
    return await response.json();
  } catch (error) {
    console.error('API Error:', error);
    return null;
  }
};

// Landing page component
const LandingPage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 font-sans">
      {/* Navigation */}
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <h1 className="text-xl font-bold text-gray-800">Beyond The Words</h1>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <Link to="/signin" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">
                Sign In
              </Link>
              <Link to="/signup" className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium">
                Sign Up
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="max-w-7xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:px-8">
        <div className="text-center">
          <h1 className="text-4xl font-extrabold text-gray-900 sm:text-5xl md:text-6xl">
            Beyond The Words
          </h1>
          <p className="mt-3 max-w-md mx-auto text-base text-gray-500 sm:text-lg md:mt-5 md:text-xl md:max-w-3xl">
            Discover insights hidden in your text with advanced analysis and visualization tools.
          </p>
          <div className="mt-5 max-w-md mx-auto sm:flex sm:justify-center md:mt-8">
            <div className="rounded-md shadow">
              <Link to="/signup" className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 md:py-4 md:text-lg md:px-10">
                Get Started
              </Link>
            </div>
            <div className="mt-3 rounded-md shadow sm:mt-0 sm:ml-3">
              <Link to="/signin" className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-blue-600 bg-white hover:bg-gray-50 md:py-4 md:text-lg md:px-10">
                Sign In
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Component for the main homepage (after login)
const Homepage = ({ stories, onStoryClick, onSearch, searchTerm, onFilter, selectedAgeGroup }) => {
  const navigate = useNavigate();
  const [showFilterDropdown, setShowFilterDropdown] = useState(false);

  const categorizeStories = () => {
    // This function will now work with the filtered stories
    const categories = {
      'Popular Stories': stories.filter(story => story.id <= 4),
      'Recommended For You': stories.filter(story => story.id > 4),
      'AI Generated': stories.filter(story => story.source === 'AI'),
      'Human Written': stories.filter(story => story.source === 'Human')
    };
    return categories;
  };

  const categories = categorizeStories();

  return (
    <div className="min-h-screen bg-gray-900 text-white font-sans">
      {/* Header */}
      <header className="bg-gray-800 p-5 flex justify-between items-center sticky top-0 z-50">
        <h1 className="text-red-500 text-3xl font-bold">Beyond the Words</h1>

        <div className="flex-1 flex justify-center mx-8">
          <div className="relative w-80">
            <input
              type="text"
              placeholder="Search stories..."
              value={searchTerm}
              onChange={(e) => onSearch(e.target.value)}
              className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-full text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
            <Search className="absolute right-3 top-2.5 h-5 w-5 text-gray-400" />
          </div>
        </div>

        <nav className="flex items-center space-x-6">
          <button onClick={() => navigate('/stories')} className="flex items-center space-x-1 hover:text-red-500 transition-colors">
            <Home className="h-5 w-5" />
            <span>Home</span>
          </button>

          <div className="relative">
            <button
              onClick={() => setShowFilterDropdown(!showFilterDropdown)}
              className="flex items-center space-x-1 hover:text-red-500 transition-colors"
            >
              <SlidersHorizontal className="h-5 w-5" />
              <span>Filter</span>
            </button>
            {showFilterDropdown && (
              <div className="absolute top-full right-0 mt-2 w-48 bg-gray-800 rounded-md shadow-lg py-1 z-20">
                <button
                  onClick={() => { onFilter('4-6 years'); setShowFilterDropdown(false); }}
                  className="block w-full text-left px-4 py-2 text-sm hover:bg-gray-700"
                >
                  4-6 years
                </button>
                <button
                  onClick={() => { onFilter('7-12 years'); setShowFilterDropdown(false); }}
                  className="block w-full text-left px-4 py-2 text-sm hover:bg-gray-700"
                >
                  7-12 years
                </button>
                <button
                  onClick={() => { onFilter('13+ years'); setShowFilterDropdown(false); }}
                  className="block w-full text-left px-4 py-2 text-sm hover:bg-gray-700"
                >
                  13+ years
                </button>
                <div className="border-t border-gray-700 my-1"></div>
                <button
                  onClick={() => { onFilter(''); setShowFilterDropdown(false); }}
                  className="block w-full text-left px-4 py-2 text-sm text-red-400 hover:bg-gray-700"
                >
                  Clear Filter
                </button>
              </div>
            )}
          </div>

          <Link to="/dashboard" className="flex items-center space-x-1 hover:text-red-500 transition-colors">
            <User className="h-5 w-5" />
            <span>Profile</span>
          </Link>
        </nav>
      </header>

      {/* Simple Video Banner */}
      <div className="relative h-96 overflow-hidden border-b-4 border-red-500 flex items-end p-16">
        <video 
          autoPlay 
          muted 
          loop 
          playsInline
          controls={false} // controls are now hidden for a cleaner look
          className="absolute inset-0 w-full h-full object-cover"
        >
          <source src="http://127.0.0.1:8000/media/hero-video.mp4" type="video/mp4" />
          <source src="http://127.0.0.1:8000/media/hero-video.webm" type="video/webm" />
          Your browser does not support the video tag.
        </video>

        {/* Fallback background */}
        <div className="absolute inset-0 bg-gradient-to-r from-purple-900 to-blue-900 -z-10"></div>

        {/* Dark overlay for text readability */}
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>

        {/* Text content */}
        <h2 className="relative z-10 text-4xl font-bold bg-black bg-opacity-60 px-6 py-3 rounded-lg">
          Discover Stories: Beyond The Words
        </h2>
      </div>

      {/* Story Categories */}
      <div className="p-8 space-y-8">
        {Object.entries(categories).map(([categoryName, categoryStories]) => (
          categoryStories.length > 0 && (
            <div key={categoryName}>
              <h3 className="text-2xl font-semibold text-red-500 mb-5 text-left">{categoryName}</h3>
              <div className="flex overflow-x-auto gap-4 pb-4" style={{scrollBehavior: 'smooth'}}>
