// Story Management Component (this will be the main stories page)
const StoryApp = () => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAgeGroup, setSelectedAgeGroup] = useState(''); // New state for age filter
  const [currentView, setCurrentView] = useState('homepage');
  const [selectedStory, setSelectedStory] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [authorshipResult, setAuthorshipResult] = useState(null);
  const [similarStories, setSimilarStories] = useState([]);
  const [loading, setLoading] = useState(false);

  // Function to apply both search and age filters
  const applyFilters = (term, age) => {
    let newFilteredStories = stories;

    if (age) {
      newFilteredStories = newFilteredStories.filter(story => story.age_group === age);
    }

    if (term) {
      newFilteredStories = newFilteredStories.filter(story =>
        story.title.toLowerCase().includes(term.toLowerCase()) ||
        story.story.toLowerCase().includes(term.toLowerCase())
      );
    }

    setFilteredStories(newFilteredStories);
  };

  // Load stories from API on component mount
  useEffect(() => {
    const fetchStories = async () => {
      const data = await apiCall('/stories/');
      if (data) {
        setStories(data);
        setFilteredStories(data);
      }
    };
    fetchStories();
  }, []);

  // Update filtered stories whenever searchTerm or selectedAgeGroup changes
  useEffect(() => {
    applyFilters(searchTerm, selectedAgeGroup);
  }, [searchTerm, selectedAgeGroup, stories]);

  // Handle story selection
  const handleStoryClick = async (story) => {
    setSelectedStory(story);
    setCurrentView('story');

    // Get similar stories
    const similar = await apiCall(`/stories/${story.id}/similar/`);
    if (similar) {
      setSimilarStories(similar);
    }
  };

  // Handle detailed analysis
  const handleDetailedAnalysis = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/analyze/`, {
        method: 'POST'
      });

      if (data) {
        setAnalysisData(data);
        setCurrentView('analysis');
      }
    } catch (error) {
      console.error('Error performing analysis:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle authorship detection
  const handleAuthorshipCheck = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/detect_authorship/`, {
        method: 'POST'
      });

      if (data) {
        setAuthorshipResult(data);
        setCurrentView('authorship');
      }
    } catch (error) {
      console.error('Error checking authorship:', error);
    } finally {
      setLoading(false);
    }
  };

  // Navigate back functions
  const handleBack = () => {
    if (currentView === 'story') {
      setCurrentView('homepage');
      setSelectedStory(null);
    } else {
      setCurrentView('story');
    }
  };

  // Handle similar story click
  const handleSimilarStoryClick = (story) => {
    handleStoryClick(story);
  };

  // Handle age group filtering
  const handleAgeFilter = (age) => {
    setSelectedAgeGroup(age);
  };

  // Render current view
  const renderCurrentView = () => {
    if (loading) {
      return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
          <div className="text-white text-xl">Loading...</div>
        </div>
      );
    }

    switch (currentView) {
      case 'homepage':
        return (
          <Homepage 
            stories={filteredStories}
            onStoryClick={handleStoryClick}
            onSearch={setSearchTerm}
            searchTerm={searchTerm}
            onFilter={handleAgeFilter}
            selectedAgeGroup={selectedAgeGroup}
          />
        );
      case 'story':
        return (
          <StoryView
            story={selectedStory}
            onBack={handleBack}
            onDetailedAnalysis={handleDetailedAnalysis}
            onAuthorshipCheck={handleAuthorshipCheck}
            similarStories={similarStories}
            onSimilarStoryClick={handleSimilarStoryClick}
          />
        );
      case 'analysis':
        return (
          <AnalysisView
            story={selectedStory}
            analysisData={analysisData}
            onBack={handleBack}
          />
        );
      case 'authorship':
        return (
          <AuthorshipView
            story={selectedStory}
            authorshipResult={authorshipResult}
            onBack={handleBack}
          />
        );
      default:
        return <div>Unknown view</div>;
    }
  };

  return <div className="StoryApp">{renderCurrentView()}</div>;
};

// MAIN APP COMPONENT - FIXED: Proper routing setup
function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<LandingPage />} />
            <Route path="/signin" element={<SignIn />} />
            <Route path="/signup" element={<SignUp />} />

            {/* Protected Routes */}
            <Route 
              path="/dashboard" 
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/stories" 
              element={
                <ProtectedRoute>
                  <StoryApp />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/admin" 
              element={
                <ProtectedRoute adminOnly={true}>
                  <SuperAdmin />
