// SearchComponent.js
import React, { useState, useEffect, useCallback } from 'react';
import { debounce } from 'lodash';
import './SearchComponent.css';

const SearchComponent = ({ onStorySelect }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [filters, setFilters] = useState({ age_group: '', source: '' });

  const availableFilters = {
    age_groups: [
      { value: '4-6', label: '4-6 years' },
      { value: '7-12', label: '7-12 years' },
      { value: '13+', label: '13+ years' }
    ],
    sources: [
      { value: 'AI', label: 'AI Generated' },
      { value: 'Human', label: 'Human Written' }
    ]
  };

  // Debounced search function - fixed useCallback dependencies
  const debouncedSearch = useCallback(
    debounce(async (query, currentFilters) => {
      if (!query.trim()) {
        setSearchResults([]);
        setShowResults(false);
        return;
      }

      setIsLoading(true);
      try {
        // Build query parameters for your existing ViewSet
        const params = new URLSearchParams();
        if (query.trim()) {
          params.append('search', query);
        }
        if (currentFilters.age_group) {
          params.append('age_group', currentFilters.age_group);
        }
        if (currentFilters.source) {
          params.append('source', currentFilters.source);
        }
        
        const response = await fetch(
          `http://localhost:8000/api/stories/?${params.toString()}`
        );
        const data = await response.json();
        
        // Handle both paginated and non-paginated responses
        const results = data.results || data;
        setSearchResults(results || []);
        setShowResults(true);
      } catch (error) {
        console.error('Search error:', error);
        setSearchResults([]);
      } finally {
        setIsLoading(false);
      }
    }, 300),
    [] // Empty dependency array since debounce creates a stable function
  );

  useEffect(() => {
    debouncedSearch(searchQuery, filters);
    
    // Cleanup function to cancel debounced calls
    return () => {
      debouncedSearch.cancel();
    };
  }, [searchQuery, filters, debouncedSearch]);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleFilterChange = (filterType, value) => {
    const newFilters = { ...filters, [filterType]: value };
    setFilters(newFilters);
  };

  const handleStoryClick = async (storyId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/stories/${storyId}/`);
      const story = await response.json();
      onStorySelect(story);
      setShowResults(false);
      setSearchQuery('');
    } catch (error) {
      console.error('Error fetching story:', error);
    }
  };

  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query})`, 'gi');
    return text.replace(regex, '<mark>$1</mark>');
  };

  const clearFilters = () => {
    setFilters({ age_group: '', source: '' });
  };

  return (
    <div className="search-container">
      <div className="search-input-container">
        <input
          type="text"
          value={searchQuery}
          onChange={handleInputChange}
          placeholder="Search stories..."
          className="search-input"
          onFocus={() => searchQuery && setShowResults(true)}
        />
        <div className="search-icon">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path
              d="M21 21L16.514 16.506M19 10.5C19 15.194 15.194 19 10.5 19S2 15.194 2 10.5 5.806 2 10.5 2 19 5.806 19 10.5Z"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </div>
        {isLoading && <div className="loading-spinner"></div>}
      </div>

      {/* Search Filters */}
      <div className="search-filters">
        <select 
          value={filters.age_group} 
          onChange={(e) => handleFilterChange('age_group', e.target.value)}
          className="filter-select"
        >
          <option value="">All Ages</option>
          {availableFilters.age_groups.map(group => (
            <option key={group.value} value={group.value}>{group.label}</option>
          ))}
        </select>
        
        <select 
          value={filters.source} 
          onChange={(e) => handleFilterChange('source', e.target.value)}
          className="filter-select"
        >
          <option value="">All Sources</option>
          {availableFilters.sources.map(source => (
            <option key={source.value} value={source.value}>{source.label}</option>
          ))}
        </select>
        
        {(filters.age_group || filters.source) && (
          <button onClick={clearFilters} className="clear-filters-btn">
            Clear Filters
          </button>
        )}
      </div>

      {showResults && (
        <div className="search-results">
          {searchResults.length === 0 ? (
            <div className="no-results">
              {searchQuery ? 'No stories found' : 'Start typing to search...'}
            </div>
          ) : (
            <div className="results-list">
              {searchResults.map((story) => (
                <div
                  key={story.id}
                  className="result-item"
                  onClick={() => handleStoryClick(story.id)}
                >
                  <h3 
                    className="result-title"
                    dangerouslySetInnerHTML={{
                      __html: highlightText(story.title, searchQuery)
                    }}
                  />
                  <div className="result-meta-top">
                    <span className="result-source">
                      {story.source === 'AI' ? 'AI Generated' : 'Human Written'}
                    </span>
                    <span className="result-age">
                      {story.age_group === '4-6' ? '4-6 years' : 
                       story.age_group === '7-12' ? '7-12 years' : '13+ years'}
                    </span>
                  </div>
                  <p 
                    className="result-excerpt"
                    dangerouslySetInnerHTML={{
                      __html: highlightText(story.story_preview || story.story?.substring(0, 150) + '...', searchQuery)
                    }}
                  />
                  <div className="result-meta">
                    <span className="result-date">
                      {new Date(story.created_at).toLocaleDateString()}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {showResults && (
        <div 
          className="search-overlay"
          onClick={() => setShowResults(false)}
        />
      )}
    </div>
  );
};

export default SearchComponent;
