import React, { useState, useEffect } from 'react';
import { Search, User, Home, BarChart3, Users, ArrowLeft, BookOpen, Brain, Filter, LogOut } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

// Mock API Base URL - replace with your actual URL
const API_BASE_URL = 'http://127.0.0.1:8000/api';

// --- Placeholder for SignIn component ---
const SignIn = ({ onLoginSuccess, onSignUpClick }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // In a real app, this would be an API call
    if (formData.email === 'test@example.com' && formData.password === 'password') {
      const mockUser = {
        name: 'Test User',
        email: 'test@example.com',
        ageGroup: '13+'
      };
      // Save to localStorage and call the parent's success handler
      localStorage.setItem('user', JSON.stringify(mockUser));
      onLoginSuccess();
    } else {
      setError('Invalid email or password.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-sm">
        <h2 className="text-3xl font-bold text-center text-red-500 mb-6">Sign In</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-400">Email</label>
            <input 
              type="email" 
              name="email" 
              value={formData.email} 
              onChange={handleInputChange} 
              required
              className="w-full px-4 py-2 mt-1 bg-gray-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
          </div>
          <div>
            <label className="block text-gray-400">Password</label>
            <input 
              type="password" 
              name="password" 
              value={formData.password} 
              onChange={handleInputChange} 
              required
              className="w-full px-4 py-2 mt-1 bg-gray-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
          </div>
          {error && <p className="text-sm text-red-400 text-center">{error}</p>}
          <button 
            type="submit"
            className="w-full px-4 py-2 mt-4 bg-red-600 rounded-lg text-white font-bold hover:bg-red-700 transition-colors"
          >
            Sign In
          </button>
        </form>
        <p className="mt-4 text-center text-gray-400">
          Don't have an account? <span onClick={onSignUpClick} className="text-red-500 cursor-pointer hover:underline">Sign Up</span>
        </p>
      </div>
    </div>
  );
};

// --- Placeholder for SignUp component ---
const SignUp = ({ onSignUpSuccess, onSignInClick }) => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '', ageGroup: '' });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // In a real app, this would be an API call to create a user
    console.log('Mock sign-up for:', formData);
    // Simulate a successful sign-up and then navigate to sign in
    onSignUpSuccess();
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-sm">
        <h2 className="text-3xl font-bold text-center text-red-500 mb-6">Sign Up</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-400">Name</label>
            <input 
              type="text" 
              name="name" 
              value={formData.name} 
              onChange={handleInputChange} 
              required
              className="w-full px-4 py-2 mt-1 bg-gray-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
          </div>
          <div>
            <label className="block text-gray-400">Email</label>
            <input 
              type="email" 
              name="email" 
              value={formData.email} 
              onChange={handleInputChange} 
              required
              className="w-full px-4 py-2 mt-1 bg-gray-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
          </div>
          <div>
            <label className="block text-gray-400">Password</label>
            <input 
              type="password" 
              name="password" 
              value={formData.password} 
