// StoryModal.js
import React from 'react';
import './StoryModal.css';

const StoryModal = ({ story, isOpen, onClose }) => {
  if (!isOpen || !story) return null;

  const formatStoryContent = (content) => {
    // Split by paragraphs and render each as a separate paragraph
    return content.split('\n').map((paragraph, index) => {
      if (paragraph.trim()) {
        return <p key={index}>{paragraph}</p>;
      }
      return null;
    });
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h1>{story.title}</h1>
          <button className="close-button" onClick={onClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path
                d="M6 6L18 18M6 18L18 6"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </button>
        </div>
        <div className="modal-meta">
          <div className="story-badges">
            <span className="source-badge">
              {story.source === 'AI' ? 'AI Generated' : 'Human Written'}
            </span>
            <span className="age-badge">
              {story.age_group === '4-6' ? '4-6 years' : 
               story.age_group === '7-12' ? '7-12 years' : '13+ years'}
            </span>
          </div>
          <p className="story-date">{new Date(story.created_at).toLocaleDateString()}</p>
        </div>
        <div className="modal-body">
          <div className="story-content">
            {formatStoryContent(story.story)}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StoryModal;
