// Main App Component
const MainApp = () => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentView, setCurrentView] = useState('homepage');
  const [selectedStory, setSelectedStory] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [authorshipResult, setAuthorshipResult] = useState(null);
  const [similarStories, setSimilarStories] = useState([]);
  const [loading, setLoading] = useState(false);

  // Modal states
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalSelectedStory, setModalSelectedStory] = useState(null);

  // Load stories from API
  useEffect(() => {
    const fetchStories = async () => {
      const data = await apiCall('/stories/');
      if (data) {
        const results = data.results || data; // Handle paginated response
        setStories(results);
        setFilteredStories(results);
      }
    };

    fetchStories();
  }, []);

  // Search functionality (keeping original for fallback)
  const handleSearch = async (term) => {
    setSearchTerm(term);
    if (!term.trim()) {
      setFilteredStories(stories);
      return;
    }

    const data = await apiCall(`/stories/search/?q=${encodeURIComponent(term)}`);
    if (data) {
      const results = data.results || data; // Handle paginated response
      setFilteredStories(results);
    }
  };

  // Handle story selection from SearchComponent (opens modal)
  const handleStorySelectFromSearch = (story) => {
    setModalSelectedStory(story);
    setIsModalOpen(true);
  };

  // Handle story click from cards (navigates to story view)
  const handleStoryClick = async (story) => {
    setSelectedStory(story);
    setCurrentView('story');

    // Get similar stories
    const similar = await apiCall(`/stories/${story.id}/similar/`);
    if (similar) {
      const results = similar.results || similar; // Handle paginated response
      setSimilarStories(results);
    }
  };

  // Handle modal close
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setModalSelectedStory(null);
  };

  // Handle detailed analysis
  const handleDetailedAnalysis = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/analyze/`, {
        method: 'POST'
      });

      if (data) {
        setAnalysisData(data);
        setCurrentView('analysis');
      }
    } catch (error) {
      console.error('Error performing analysis:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle authorship detection
  const handleAuthorshipCheck = async () => {
    setLoading(true);
    try {
      const data = await apiCall(`/stories/${selectedStory.id}/detect_authorship/`, {
        method: 'POST'
      });

      if (data) {
        setAuthorshipResult(data);
        setCurrentView('authorship');
      }
    } catch (error) {
      console.error('Error checking authorship:', error);
    } finally {
      setLoading(false);
    }
  };

  // Navigate back functions
  const handleBack = () => {
    if (currentView === 'story') {
      setCurrentView('homepage');
      setSelectedStory(null);
    } else {
      setCurrentView('story');
    }
  };

  // Handle similar story click
  const handleSimilarStoryClick = (story) => {
    handleStoryClick(story);
  };

  // Render current view
  const renderCurrentView = () => {
    if (loading) {
      return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
          <div className="text-white text-xl">Loading...</div>
        </div>
      );
    }

    switch (currentView) {
      case 'homepage':
        return (
          <Homepage 
            stories={filteredStories}
            onStoryClick={handleStoryClick}
            onSearch={handleSearch}
            searchTerm={searchTerm}
            onStorySelect={handleStorySelectFromSearch}
            isModalOpen={isModalOpen}
            selectedStory={modalSelectedStory}
            onCloseModal={handleCloseModal}
          />
        );
      case 'story':
        return (
          <StoryView
            story={selectedStory}
            onBack={handleBack}
            onDetailedAnalysis={handleDetailedAnalysis}
            onAuthorshipCheck={handleAuthorshipCheck}
            similarStories={similarStories}
            onSimilarStoryClick={handleSimilarStoryClick}
          />
        );
      case 'analysis':
        return (
          <AnalysisView
            story={selectedStory}
            analysisData={analysisData}
            onBack={handleBack}
          />
        );
      case 'authorship':
        return (
          <AuthorshipView
            story={selectedStory}
            authorshipResult={authorshipResult}
            onBack={handleBack}
          />
        );
      default:
        return <div>Unknown view</div>;
    }
  };

  return <div className="App">{renderCurrentView()}</div>;
};

